using System.Net.Http;
using System.Threading.Tasks;

var client = new HttpClient();
client.BaseAddress = new System.Uri("https://api.gameball.co");
client.DefaultRequestHeaders.Add("apikey", "YOUR_API_KEY");
client.DefaultRequestHeaders.Add("secretkey", "YOUR_SECRET_KEY");
var request = new HttpRequestMessage(HttpMethod.Delete, "/api/v4.0/integrations/customers/12345");
var response = await client.SendAsync(request);
response.EnsureSuccessStatusCode();

using System.Net.Http;
var client = new HttpClient();
client.DefaultRequestHeaders.Add("apikey", "YOUR_API_KEY");
client.DefaultRequestHeaders.Add("secretkey", "YOUR_SECRET_KEY");
var res = await client.DeleteAsync("https://api.gameball.co/api/v4.0/integrations/customers/12345/tags/vip");
res.EnsureSuccessStatusCode();

using System.Net.Http;
var client = new HttpClient();
client.DefaultRequestHeaders.Add("apikey", "YOUR_API_KEY");
client.DefaultRequestHeaders.Add("secretkey", "YOUR_SECRET_KEY");
var res = await client.GetAsync("https://api.gameball.co/api/v4.0/integrations/customers/12345/notifications");
res.EnsureSuccessStatusCode();

using System.Net.Http; using System.Text;
var client = new HttpClient();
client.DefaultRequestHeaders.Add("apikey", "YOUR_API_KEY");
client.DefaultRequestHeaders.Add("secretkey", "YOUR_SECRET_KEY");
var content = new StringContent("{\"notificationIds\":[\"n_01\",\"n_02\"]}", Encoding.UTF8, "application/json");
var res = await client.PostAsync("https://api.gameball.co/api/v4.0/integrations/customers/12345/notifications/read", content);
res.EnsureSuccessStatusCode();
